package while_;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//while == mientras
		
		
		
		//declaramos variables
		int i=1;
		
		//creamos el while 
		//establecemos condicion
		while (i<=1000) {
			System.out.println(i);//damos ordenes y hasta que no se cumple la condicion, el bucle no parara
			i++; //esto sirve para sumar 1 a i
		}
		
		while (i>=-10) {
			System.out.println(i);//damos ordenes y hasta que no se cumple la condicion, el bucle no parara
			i--; //esto sirve para sumar 1 a i
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
